const axios = require('axios');


export function getAllPokemon() {     
    return async function(dispatch) {         // store.dispatch()
        let promise = await axios.get(`https://pokeapi.co/api/v2/pokemon?limit=151%27`)
        let response = promise.data;
        dispatch({ type: "GET_ALL_POKEMON", payload: response });
    };
}

export function setSelected(data) {   
    return function(dispatch) {         // store.dispatch()
        dispatch({ type: "RENDER", payload: data});
    };
}

export function setRender(data) {   
    return function(dispatch) {         // store.dispatch()
        dispatch({ type: "SELECTED", payload: data});
    };
}

export function getPokemonDetail(data) {     // buscamos una especifica por ID. Avengers tiene muchas peliculas, pero el ID es unico para una
    return function(dispatch) {         // store.dispatch()
        fetch(``)
        .then(response => response.json())
        .then(data => {                         // guarda aca la data que le mande a buscar
        dispatch({ type: "GET_POKEMON_DETAIL", payload: data });     // paso la data que busque a la redux function
        });
    };
}
