const initialState = {
    pokemonLoaded: [],
    pokemonDetail: {}
  };


  function rootReducer(state = initialState, action) {
    if (action.type === "GET_ALL_POKEMON") {
        return {
          ...state,
          pokemonLoaded: action.payload.results         // results es una propiedad que me devolvio la API en payload
        };
    }
    if (action.type === "GET_POKEMON_DETAIL") {
        return {
          ...state,
          pokemonDetail: action.payload        
        };
    }
    return state;
  }
  
  export default rootReducer;
