import React, { useState } from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import './Buscador.css';
import { addMovieFavorite, getMovies } from "../../actions";



const Buscador = () => {
  const [state, setState] = useState('');

  function handleChange(event) {
    setState({ title: event.target.value });
  }
  
  function handleSubmit(event) {
    event.preventDefault();
    getMovies(state.title);    // invoco a getmovies mediante las props de la clase, lo mismo con el titulo
  }

  
    const { title } = state;
    return (
      <div>
        <h2>Buscador</h2>
        <form className="form-container" onSubmit={(e) => handleSubmit(e)}>
          <div>
            <label className="label" htmlFor="title">Pel√≠cula: </label>
            <input
              type="text"
              id="title"
              autoComplete="off"
              value={title}
              onChange={(e) => handleChange(e)}
            />
          </div>
          <button type="submit">BUSCAR</button>
        </form>
        <ul>
         {/* Aqui tienes que escribir tu codigo para mostrar la lista de peliculas */}
         {state.movies?.map(m=> (
           <div>
             <Link to= {`/movie/${m.imdbID}`}>
              <li>{m.Title}</li>
            </Link>
            <button onClick={() => addMovieFavorite(m)}>Fav</button>  {/* No puedo pasar una funcion ejecutada, para eso uso arrow function */}
          </div>
         ))}
        </ul>
      </div>
    );
  
}

function mapStateToProps(state) {        // recibe la parte del estado que necesitamos
  return {
    movies: state.moviesLoaded          // esto lo sacamos DEL STORE PORQUE ESTAS BUSCANDO LA DATA AL STORE
  };
}


export default connect(
  mapStateToProps, { getMovies, addMovieFavorite }   // pasamos los action creators que voy a queres despachar desde mi componente, o la funcion dispatchpropstonoseque
)(Buscador);
