import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import { removeMovieFavorite } from "../../actions";
import './Favorites.css';

export class ConnectedList extends Component {
  

  render() {
    return (
      <div>
        <h2>Pel√≠culas Favoritas</h2>
        <ul>
          {/* Aqui deberias poner tu lista de peliculas! */}
          {
            this.props.movies?.map(m =>(
              <div key={m.imdbID}>
             <Link to= {`/movie/${m.imdbID}`}>
              <li>{m.Title}</li>
            </Link>
            <button onClick={() => this.props.removeMovieFavorite(m.imdbID)}>X</button>  {/* No puedo pasar una funcion ejecutada, para eso uso arrow function */}
          </div>
            ))
          }
        </ul>
      </div>
    );
  }
}

const mapStateToProps = (state) =>{
  return{
    movies: state.moviesFavourites         // ahora me traje el array de peliculas favoritas, asi que las tengo en this.props.movies
  }
}

export default connect(mapStateToProps)(ConnectedList);
