import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import s from './Pokemon.module.css';
import { useDispatch } from 'react-redux';

export default function Pokemon({pokemon}){
    const dispatch = useDispatch();
    const { id } = useParams();

    const capitalizeName = function(name){
        let ans = name.charAt(0).toUpperCase() + name.slice(1);
        if (ans === 'Mr-mime'){
          ans = 'Mr. Mime';
        }
        return ans;
    } 


    return (
        <div> {pokemon.forms ? (<div>
                <div className={s.screen}>
                    <div className={s.header}>
                        <h3  className={s.title}>{capitalizeName(pokemon.forms[0].name)}</h3>
                        <h4 className={s.id}>NÂº {id}</h4>
                    </div>
                    <img className={s.img} src={`https://www.pkparaiso.com/imagenes/espada_escudo/sprites/animados-gigante/${pokemon.forms[0].name}.gif`}/>
                </div>
                <div className={s.data}>
                    <div className={s.stats}>
                        <p></p>
                    </div>
                    <div className={s.types} > 
                        <div className={s.type1}>{capitalizeName(pokemon.types[0].type.name)}</div>
                        { pokemon.stats[1].type && <div className={s.type2}>{capitalizeName(pokemon.types[1].type.name)}</div>}
                    </div>
                </div>
            </div>
            ) :   null
            }
        </div>
    );
}
