import { useSelector, useDispatch } from 'react-redux';
import React, { useEffect, useState } from "react";
import s from './Buscador.module.css';
import background from '../../Images/background.gif';
import pokedex from '../../Images/POKEDEX.png';
import { getAllPokemon, getPokemonDetail }  from '../../actions/index.js';
import Card from '../Card/Card';


export default function Buscador(){
  const allPokemon = useSelector((state)=> state.pokemonLoaded);
  const dispatch = useDispatch();
  const [state, setState] = useState({title: ''});
  const [selected, setSelected] = useState({id: 1});
  const [render, setRender] = useState([]);

  useEffect(() => { // Get the pokemons with a component did mount
    dispatch(getAllPokemon());
  }, []);

  useEffect(() => {  // once the initial dispatch has been used, set the render to the first 8 pokemons
    setRender([{name: allPokemon[0].name},{name: allPokemon[1].name},{name: allPokemon[2].name},{name: allPokemon[3].name},{name: allPokemon[4].name},{name: allPokemon[5].name},{name: allPokemon[6].name},{name: allPokemon[7].name}])
  }, [dispatch]);



  return (
    <div style={{position: 'relative'}}>
      <div style={{position: 'absolute', backgroundImage: `url(${background})`, backgroundPosition: 'center' , backgroundSize: 'cover',  width: '100vw', height: '100vh'}}/>
      <img className={s.pokedex} src={pokedex}/>
          <Card name={render[0].name} />
    </div>
  );
  
}
