    if (selected>1){
      setSelected(prevSelected=>(prevSelected-1));
    }
  }

  const handleRight = function(){
    if (selected<151){
      setSelected(prevSelected=>(prevSelected+1));
    }
  }

  const handleDown = function(){
    if (selected<148){
      setSelected(prevSelected=>(prevSelected+4));
    }
  }

  useEffect(() => { 
    handleRender('up');
  }, [handleUp]);

  useEffect(() => { 
    handleRender('left');
  }, [handleLeft]);

  useEffect(() => {
    handleRender('right');
  }, [handleRight]);

  useEffect(() => {
    handleRender('down');
  }, [handleDown]);

  const handleRender = function(arrow){

