import { useSelector, useDispatch } from 'react-redux';
import React, { useEffect, useState } from "react";
import s from './Buscador.module.css';
import background from '../../Images/background.gif';
import pokedex from '../../Images/POKEDEX.png';
import { getAllPokemon, getPokemonDetail, setRender }  from '../../actions/index.js';
import Cards from '../Cards/Cards';


export default function Buscador(){
  const dispatch = useDispatch();
  const allPokemon = useSelector((state)=> state.pokemonLoaded);
  const render = useSelector((state)=> state.pokemonRender);
  const [selected, setSelected] = useState(1);
  const [state, setState] = useState({title: ''});
  console.log(selected);

  useEffect(() => { // Get the pokemons with a component did mount
    dispatch(getAllPokemon());
  }, []);

  const handleUp = function(){
    if (selected>=5){
      setSelected(prevSelected=>(prevSelected-4));
    }
  }

  const handleLeft = function(){
    if (selected>1){
      setSelected(prevSelected=>(prevSelected-1));
    }
  }

  const handleRight = function(){
    if (selected<151){
      setSelected(prevSelected=>(prevSelected+1));
    }
  }

  const handleDown = function(){
    if (selected<148){
      setSelected(prevSelected=>(prevSelected+4));
      handleRender('down');
    }
  }

  useEffect(() => { // Get the pokemons with a component did mount
    handleRender('up');
  }, [handleUp]);

  useEffect(() => { // Get the pokemons with a component did mount
    handleRender('left');
  }, [handleLeft]);

  useEffect(() => { // Get the pokemons with a component did mount
    handleRender('right');
  }, [handleRight]);

  useEffect(() => { // Get the pokemons with a component did mount
    handleRender('down');
  }, [handleDown]);

  const handleRender = function(arrow){
    console.log(render);
    if(arrow === 'down' || arrow ==='right'){
      let isRendered = false;
      for(let i=0; i<render.length;i++){
        if (render[i].id === selected){
          isRendered=true;
        }
      }
      if(!isRendered){
        dispatch(setRender(allPokemon.slice(4,12)));
        //dispatch(setRender(allPokemon.slice(render[0].id+4,render[7].id+4)));
      }
    }
    if(arrow === 'up' || arrow ==='left'){
      let isRendered = false;
      for(let i=0; i<render.length;i++){
        if (render[i].id === selected){
          isRendered=true;
        }
      }
      if(!isRendered){
        dispatch(setRender(allPokemon.slice(0,8)));
      }
    }
  }

  return (
    <div style={{position: 'relative'}}>
      <div style={{position: 'absolute', backgroundImage: `url(${background})`, backgroundPosition: 'center' , backgroundSize: 'cover',  width: '100vw', height: '100vh'}}/>
      <img className={s.pokedex} src={pokedex}/>
        <Cards selected={selected}/>
      <div className={s.dpad}>
        <div>
          <button onClick={handleUp} className={s.up} >.</button>
        </div>
        <div>
          <button onClick={handleLeft} className={s.left}  >.</button>
          <button onClick={handleRight} className={s.right}>.</button>
        </div>
        <div>
        <button onClick={handleDown} className={s.down}>.</button>
        </div>
      </div>
    </div>
  );
  
}
