import React from 'react';
import { connect } from 'react-redux';
import { getMovieDetail } from '../../actions/index';

import './Movie.css';

class Movie extends React.Component {

    // this.props.match.params.id me va a dar el id de la pelicula
    // this.props es lo que recibo. match.params es para obtener los parametros pasados por ruta. pase id, por ende this.props.match.params.id

    componentDidMount(){         // la primera vez que se monte (usar hooks en caso de componente funcion)
        this.props.getMovieDetail(this.props.match.params.id)  // buscame la data de la peli por id
    }

    render() {
        return (
            <div className="movie-detail">
                <h4>Que onda perri esta es tu peli</h4>
                <h3>{this.props.movie.Title}</h3>
                <h5>{this.props.movie.Director}</h5>
                <h5>{this.props.movie.Year}</h5>
                <p>{this.props.movie.Plot}</p>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return{
        movie: state.movieDetail        // esto lo sacamos DEL STORE PORQUE ESTAS BUSCANDO LA DATA AL STORE
    }
}


export default connect(mapStateToProps, { getMovieDetail })(Movie);
