import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import s from './Pokemon.module.css';
import { useDispatch } from 'react-redux';

export default function Pokemon({pokemon}){
    const dispatch = useDispatch();
    const { id } = useParams();

    return (
        <div> {pokemon.forms ?
            <div className={s.screen}>
                <h3>{pokemon.forms[0].name}</h3>
                <h4>NÂº {id}</h4>
                {
                    pokemon.forms ? <img className={s.img} src={`https://www.pkparaiso.com/imagenes/espada_escudo/sprites/animados-gigante/${pokemon.forms[0].name}.gif`}/> : null
                }
            </div>
            :   <div className={s.loadingScreen}>
                    <img className={s.img} src={`https://cdn.dribbble.com/users/1771704/screenshots/6124573/pokeball-800x600.gif`}/>
                </div>
            }
        </div>
    );
    
}
