export function getAllPokemon() {
    return function(dispatch) {
        return fetch(`https://pokeapi.co/api/v2/pokemon?limit=151%27`)
        .then(response => response.json())
        .then(json => {
        dispatch({ type: "GET_ALL_POKEMON", payload: json });
        });
    };
}

export function getMovieDetail(idMovie) {     // buscamos una especifica por ID. Avengers tiene muchas peliculas, pero el ID es unico para una
    return function(dispatch) {         // store.dispatch()
        fetch(`http://www.omdbapi.com/?apikey=${apiKey}&i=${idMovie}`)
        .then(response => response.json())
        .then(data => {                         // guarda aca la data que le mande a buscar
        dispatch({ type: "GET_MOVIE_DETAILS", payload: data });     // paso la data que busque a la redux function
        });
    };
}
