let apiKey = '6d022ee2';

export function addMovieFavorite(payload) {
    return { type: "ADD_MOVIE_FAVORITE", payload };
}

export function removedMovieFavorite(idMovie) {   // saca la pelicula idMovie
    return { type: "REMOVE_MOVIE_FAVORITE",
     payload: idMovie    // cualquier nombre: pelicula a remover
    };
}

export function getMovies(titulo) {
    return function(dispatch) {
        return fetch(`http://www.omdbapi.com/?apikey=${apiKey}&s=${titulo}`)
        .then(response => response.json())
        .then(json => {
        dispatch({ type: "GET_MOVIES", payload: json });
        });
    };
}

export function getMovieDetail(idMovie) {     // buscamos una especifica por ID. Avengers tiene muchas peliculas, pero el ID es unico para una
    return function(dispatch) {         // store.dispatch()
        fetch(`http://www.omdbapi.com/?apikey=${apiKey}&i=${idMovie}`)
        .then(response => response.json())
        .then(data => {                         // guarda aca la data que le mande a buscar
        dispatch({ type: "GET_MOVIE_DETAIL", payload: data });     // paso la data que busque a la redux function
        });
    };
}
