import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import './Buscador.css';
import { addMovieFavorite, getMovies } from "../../actions";
import { bindActionCreators } from 'redux';
import * as actionsCreators from '../../actions/index';



export class Buscador extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: ""
    };
  }

  handleChange(event) {
    this.setState({ title: event.target.value });
  }
  
  handleSubmit(event) {
    event.preventDefault();
    this.props.getMovies(this.state.title);    // invoco a getmovies mediante las props de la clase, lo mismo con el titulo
  }

  render() {
    const { title } = this.state;
    return (
      <div>
        <h2>Buscador</h2>
        <form className="form-container" onSubmit={(e) => this.handleSubmit(e)}>
          <div>
            <label className="label" htmlFor="title">Película: </label>
            <input
              type="text"
              id="title"
              autoComplete="off"
              value={title}
              onChange={(e) => this.handleChange(e)}
            />
          </div>
          <button type="submit">BUSCAR</button>
        </form>
        <ul>
         {/* Aqui tienes que escribir tu codigo para mostrar la lista de peliculas */}
         {this.props.movies?.map(m=> (
           <div>
             <Link to= {`/movie/${m.imdbID}`}>
              <li>{m.Title}</li>
            </Link>
            <button onClick={() => this.props.addMovieFavorite(m)}>Fav</button>  {/* No puedo pasar una funcion ejecutada, para eso uso arrow function */}
          </div>
         ))}
        </ul>
      </div>
    );
  }
}

function mapStateToProps(state) {        // recibe la parte del estado que necesitamos
  return {
    movies: state.moviesLoaded          // esto lo sacamos DEL STORE PORQUE ESTAS BUSCANDO LA DATA AL STORE
  };
}

function mapDispatchToProps(dispatch) {
	return bindActionCreators(actionsCreators, dispatch);   // me genera funciones dispatch para cada actionCreator (linea 3)
}


export default connect(
  mapStateToProps, mapDispatchToProps   // pasamos los action creators que voy a queres despachar desde mi componente, o la funcion dispatchpropstonoseque
)(Buscador);





