const initialState = {
    moviesFavourites: [],
    moviesLoaded: [],
    movieDetail: {}
  };


  function rootReducer(state = initialState, action) {
    if (action.type === "ADD_MOVIE_FAVORITE") {
        return {
          ...state,
          moviesFavourites: state.moviesFavourites.concat(action.payload)
        }
    }
    if (action.type === "GET_MOVIES") {
        return {
          ...state,
          moviesLoaded: action.payload.Search         // Search es una propiedad que me devolvio la API en payload
        };
    }
    if (action.type === "REMOVE_MOVIE_FAVORITE") {
        return {
          ...state,
          moviesFavourites: state.moviesFavourites.filter(m => m.imdbID !== action.payload)  // le seteo a favourites todas las peliculas que NO coincidan con la que quiero sacar
        }
    }
    if (action.type === "GET_MOVIE_DETAILS") {
        return {
          ...state,
          movieDetail: action.payload            // uso action cuando trabajo con la data que se me pasa. Uso state cuando trabajo con el estado
        };
    }
    return state;
  }
  
  export default rootReducer;
